            if (is_array($args[0])) {
                return $this->BotAPI($name, $args[0]);
            }
            $argsAPI = [];
            $i = 0;
            foreach ($methodsList[$name] as $field) {
                $argsAPI[$field['name']] = $args[$i];
                $i++;
            }
            return $this->BotAPI($name, $argsAPI);
        } else {
            throw new \neneone\neneone\Exception('Method not found');
        }
